x;\r\n	margin: -10px;\r\n	position: absolute;\r\n	text-align: center;\r\n	width: 20px;\r\n}\r\n\r\n.clock-numbers li:nth-child(1) {\r\n	left: 50%;\r\n	top: 0;\r\n}\r\n\r\n.clock-numbers li:nth-child(2) {\r\n	right: 0;\r\n	top: 50%;\r\n}\r\n\r\n.clock-numbers li:nth-child(3) {\r\n	bottom: 0;\r\n	left: 50%;\r\n}\r\n\r\n.clock-numbers li:nth-child(4) {\r\n	left: 0;\r\n	top: 50%;\r\n}', '(function() {\r\n\r\n	var clockSeconds = document.getElementById(''clock-seconds''),\r\n	clockMinutes = document.getElementById(''clock-minutes''),\r\n	clockHours = document.getElementById(''clock-hours'');\r\n\r\n	function getTime() {\r\n\r\n		var date = new Date(),\r\n		seconds = date.getSeconds(),\r\n		minutes = date.getMinutes(),\r\n		hours = date.getHours(),\r\n\r\n		degSeconds = seconds * 360 / 60,\r\n		degMinutes = (minutes + seconds / 60) * 360 / 60,\r\n		degHours = (hours + minutes / 60 + seconds / 60 / 60) * 360 / 12;\r\n\r\n		clockSeconds.setAttribute(''style'', ''-webkit-transform: rotate('' + degSeconds + ''deg); -moz-transform: rotate('' + degSeconds + ''deg); -ms-transform: rotate('' + degSeconds + ''deg); -o-transform: rotate('' + degSeconds + ''deg); transform: rotate('' + degSeconds + ''deg);'');\r\n		clockMinutes.setAttribute(''style'', ''-webkit-transform: rotate('' + degMinutes + ''deg); -moz-transform: rotate('' + degMinutes + ''deg); -ms-transform: rotate('' + degMinutes + ''deg); -o-transform: rotate('' + degMinutes + ''deg); transform: rotate('' + degMinutes + ''deg);'');\r\n		clockHours.setAttribute(''style'', ''-webkit-transform: rotate('' + degHours + ''deg); -moz-transform: rotate('' + degHours + ''deg); -ms-transform: rotate('' + degHours + ''deg); -o-transform: rotate('' + degHours + ''deg); transform: rotate('' + degHours + ''deg);'');\r\n	}\r\n\r\n	setInterval(getTime, 1000);\r\n	getTime();\r\n\r\n} ());'),
(3, 1, 'Css3 SlideShow', '<ul id=''css3-slider''>\r\n    <li>\r\n        <input type=''radio'' id=''s1'' name=''num'' checked=''true'' />\r\n        <label for=''s1''>1</label>\r\n        <a href=''#''>\r\n            <img src=''data/gambar/slide-1.jpg'' />\r\n            <span>Why do you put the egg yolks on your eyes?</span>\r\n         </a >\r\n    </li>\r\n    <li>\r\n        <input type=''radio'' id=''s2'' name=''num'' />\r\n        <label for=''s2''>2</label>\r\n        <a href=''#''>\r\n            <img src=''data/gambar/slide-2.jpg'' />\r\n            <span>How can you look ahead if your eyes are on the side?</span>\r\n        </a>\r\n    </li>\r\n    <li>\r\n        <input type=''radio'' id=''s3'' name=''num'' />\r\n        <label for=''s3''>3</label>\r\n        <a href=''#''>\r\n            <img src=''data/gambar/slide-3.jpg'' />\r\n            <span>Keep praying to God</span>\r\n        </a>\r\n    </li>\r\n    <li>\r\n        <input type=''radio'' id=''s4'' name=''num'' />\r\n        <label for=''s4''>4</label>\r\n        <a href=''#''>\r\n            <img src=''data/gambar/slide-4.jpg'' />\r\n            <span>Stay relaxed</span>\r\n        </a>\r\n    </li>\r\n    <li>\r\n        <input type=''radio'' id=''s5'' name=''num'' />\r\n        <label for=''s5''>5</label>\r\n        <a href=''#''>\r\n            <img src=''data/gambar/slide-5.jpg'' />\r\n            <span>And you will forever be relaxed</span>\r\n        </a>\r\n    </li>\r\n</ul>', '/* General */\r\nbody{\r\n    background:#efefef;\r\n}\r\n#css3-slider {\r\n  margin:100px auto;\r\n  padding:0px 0px;\r\n  width:448px;\r\n  height:286px;\r\n  position:relative;\r\n  box-shadow:0px 0px 3px #000;\r\n}\r\n\r\n#css3-slider li {\r\n  list-style:none;\r\n  margin:0px 0px;\r\n  padding:0px 0px;\r\n}\r\n\r\n/* Navigation */\r\n#css3-slider li input + label {\r\n  position:absolute;\r\n  bottom:5px;\r\n  left:10px;\r\n  z-index:999;\r\n  font:bold 11px/16px Arial,Sans-Serif;\r\n  background-color:black;\r\n  color:white;\r\n  padding:0px 0px;\r\n  width:16px;\r\n  text-align:center;\r\n  cursor:pointer;\r\n}\r\n\r\n#css3-slider li:nth-child(2) label {left:28px;}\r\n#css3-slider li:nth-child(3) label {left:46px;}\r\n#css3-slider li:nth-child(4) label {left:64px;}\r\n#css3-slider li:nth-child(5) label {left:82px;}\r\n\r\n/* Images */\r\n#css3-slider li img {\r\n  border:none;\r\n  outline:none;\r\n  position:absolute;\r\n  top:50%;\r\n  left:50%;\r\n  width:0px;\r\n  height:0px;\r\n  visibility:hidden;\r\n  opacity:0;\r\n  -webkit-transition:all 2s ease-in-out;\r\n  -moz-transition:all 2s ease-in-out;\r\n  -ms-transition:all 2s ease-in-out;\r\n  -o-transition:all 2s ease-in-out;\r\n  transition:all 2s ease-in-out;\r\n  -webkit-transform:rotate(0deg) scale(0);\r\n  -moz-transform:rotate(0deg) scale(0);\r\n  -ms-transform:rotate(0deg) scale(0);\r\n  -o-transform:rotate(0deg) scale(0);\r\n  transform:rotate(0deg) scale(0);\r\n}\r\n\r\n/* Captions */\r\n#css3-slider a {\r\n  text-decoration:none !important;\r\n}\r\n\r\n#css3-slider li a span {\r\n  display:block;\r\n  position:absolute;\r\n  right:0px;\r\n  bottom:0px;\r\n  left:0px;\r\n  background-color:rgba(0,0,0,0.8);\r\n  font:normal 11px/26px Arial,Sans-Serif;\r\n  color:white;\r\n  padding:0px 10px;\r\n  text-align:right;\r\n  opacity:0;\r\n  viibility:hidden;\r\n  -webkit-transition:all 2s ease-in-out;\r\n  -moz-transition:all 2s ease-in-out;\r\n  -ms-transition:all 2s ease-in-out;\r\n  -o-transition:all 2s ease-in-out;\r\n  transition:all 2s ease-in-out;\r\n}\r\n\r\n/* Active navigation */\r\n#css3-slider li input:checked + label {\r\n  background-color:#39f;\r\n  color:white;\r\n}\r\n\r\n/* Show the image with transition */\r\n#css3-slider li input:checked ~ img,\r\n#css3-slider li input:checked ~ a img {\r\n  top:0%;\r\n  left:0%;\r\n  width:448px;\r\n  height:286px;\r\n  visibility:visible;\r\n  -webkit-transform:rotate(720deg) scale(1);\r\n  -moz-transform:rotate(720deg) scale(1);\r\n  -ms-transform:rotate(720deg) scale(1);\r\n  -o-transform:rotate(720deg) scale(1);\r\n  transform:rotate(720deg) scale(1);\r\n  opacity:1;\r\n  z-index:99;\r\n}\r\n\r\n/* Show the caption with fade effect */\r\n#css3-slider li input:checked ~ a span {\r\n  opacity:1;\r\n  z-index:100;\r\n}\r\n\r\n/* Hide the radio */\r\n#css3-slider input {\r\n  display:none;\r\n}', ''),
(4, 1, 'Kotak Melayang', '<div id=''kotak''>\r\n    <span>A</span>\r\n    <span>B</span>\r\n    <span>C</span>\r\n    <span>D</span>\r\n</div>', 'body {\r\n    background-color:#eee;\r\n    text-align:center;\r\n}\r\n#kotak{\r\n    padding:50px;\r\n}\r\n#kotak span {\r\n    display:inline-block;\r\n    position:relative;\r\n    background-color:white;\r\n    width:70px;\r\n    height:70px;\r\n    margin:0 5px;\r\n    -webkit-box-shadow:0 10px 5px -4px rgba(0,0,0,.2);\r\n    -moz-box-shadow:0 10px 5px -4px rgba(0,0,0,.2);\r\n    box-shadow:0 10px 5px -4px rgba(0,0,0,.2);\r\n    -webkit-border-radius:10px;\r\n    -moz-border-radius:10px;\r\n    border-radius:10px;\r\n    font:normal normal 30px/70px "Comic Sans MS",Verdana,Arial,Sans-Serif;\r\n    text-align:center;\r\n    color:#888;\r\n    cursor:default;\r\n}\r\n\r\nspan:hover {\r\n    top:5px;\r\n    -webkit-box-shadow:0 2px 2px rgba(0,0,0,.2);\r\n    -moz-box-shadow:0 2px 2px rgba(0,0,0,.2);\r\n    box-shadow:0 2px 2px rgba(0,0,0,.2);\r\n}\r\n\r\nspan:active {\r\n    top:6px;\r\n    -webkit-box-shadow:0 1px 2px rgba(0,0,0,.2);\r\n    -moz-box-shadow:0 1px 2px rgba(0,0,0,.2);\r\n    box-shadow:0 1px 2px rgba(0,0,0,.2);\r\n}', '');
