\n.outro {\r\n	background-color: #1f1f24;\r\n	margin: 0px;\r\n	border: 5px solid #c16;\r\n	border-radius: 0px;\r\n	width: auto;\r\n	height: auto;\r\n	box-shadow: 0 0 18px hsla(0,0%,0%,.5);\r\n	-webkit-animation: rounddd 8s linear infinite;\r\n  animation: rounddd 8s linear infinite;\r\n	transition: border-radius 0.3s linear;\r\n}\r\n\r\n.outro:hover {\r\n  border-radius: 40%; \r\n}\r\n\r\n@-webkit-keyframes round {\r\n0% {-webkit-transform: rotate(720deg);\r\n	transform: rotate(720deg);\r\n}\r\n\r\n100% {-webkit-transform: rotate(360deg);\r\n	  transform: rotate(360deg);\r\n}\r\n}\r\n\r\n@-webkit-keyframes rounddd {\r\n0% {-webkit-transform: rotate(0deg);;\r\n	transform: rotate(0deg);\r\n}\r\n  \r\n100% {-webkit-transform: rotate(720deg);\r\n	  transform: rotate(720deg);\r\n}\r\n}', ''),
(7, 1, 'Glosy DropDown', '<div class="glossy-selectbox">\r\n    <input type="checkbox">\r\n    <label data-default="Share This Post!" data-focus="Select one of the social media service..."></label>\r\n    <ul>\r\n        <li><a class="social-rss" href="#">RSS Feed</a></li>\r\n        <li><a class="social-facebook" href="#">Facebook</a></li>\r\n        <li><a class="social-twitter" href="#">Twitter</a></li>\r\n        <li><a class="social-google" href="#">Google+</a></li>\r\n    </ul>\r\n</div>', '.glossy-selectbox {\r\n  display:inline-block;\r\n  font:normal bold 12px Arial,Sans-Serif;\r\n  position:relative;\r\n  margin:200px 32%;\r\n  width:300px;\r\n  background-color:#111;\r\n  text-align:left;\r\n  /* CSS3 Browser */\r\n  background-image:-webkit-linear-gradient(top,rgba(255,255,255,.4) 0%,rgba(255,255,255,.2) 50%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);\r\n  background-image:-moz-linear-gradient(top,rgba(255,255,255,.4) 0%,rgba(255,255,255,.2) 50%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);\r\n  background-image:-ms-linear-gradient(top,rgba(255,255,255,.4) 0%,rgba(255,255,255,.2) 50%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);\r\n  background-image:-o-linear-gradient(top,rgba(255,255,255,.4) 0%,rgba(255,255,255,.2) 50%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);\r\n  background-image:linear-gradient(top,rgba(255,255,255,.4) 0%,rgba(255,255,255,.2) 50%,rgba(255,255,255,0) 50%,rgba(255,255,255,.1) 100%);\r\n  /* IE only */\r\n  filter:progid:DXImageTransform.Microsoft.gradient(gradientType=0,startColorstr=''#333333'',endColorstr=''#111111'');\r\n  -webkit-border-radius:7px;\r\n  -moz-border-radius:7px;\r\n  border-radius:7px;\r\n  -webkit-box-shadow:inset 0 1px 1px 1px rgba(255,255,255,.1),0 1px 3px rgba(0,0,0,.7);\r\n  -moz-box-shadow:inset 0 1px 1px 1px rgba(255,255,255,.1),0 1px 3px rgba(0,0,0,.7);\r\n  box-shadow:inset 0 1px 1px 1px rgba(255,255,255,.1),0 1px 3px rgba(0,0,0,.7);\r\n}\r\n\r\n.glossy-selectbox:before,\r\n.glossy-selectbox:after {\r\n  content:"";\r\n  display:block;\r\n  width:0;\r\n  height:0;\r\n  border:3px solid transparent;\r\n  border-width:5px 3px;\r\n  border-bottom-color:#999;\r\n  position:absolute;\r\n  top:25%;\r\n  right:5px;\r\n  z-index:4;\r\n}\r\n\r\n.glossy-selectbox:after {\r\n  border-color:#999 transparent transparent;\r\n  top:auto;\r\n  bottom:25%;\r\n}\r\n\r\n.glossy-selectbox input {\r\n  display:block;\r\n  position:absolute;\r\n  top:0;\r\n  right:0;\r\n  bottom:0;\r\n  width:15px;\r\n  height:100%;\r\n  opacity:0;\r\n  z-index:10;\r\n  cursor:pointer;\r\n}\r\n\r\n.glossy-selectbox label {\r\n  display:block;\r\n  line-height:45px;\r\n  color:rgba(255,255,255,.5);\r\n  padding:0 15px;\r\n  -webkit-transition:all 0s ease-out;\r\n  -moz-transition:all 0s ease-out;\r\n  -ms-transition:all 0s ease-out;\r\n  -o-transition:all 0s ease-out;\r\n  transition:all 0s ease-out;\r\n}\r\n\r\n.glossy-selectbox label:before {\r\n  content:attr(data-default);\r\n}\r\n\r\n.glossy-selectbox label:after {\r\n  content:"";\r\n  display:block;\r\n  position:absolute;\r\n  top:0;\r\n  right:0;\r\n  bottom:0;\r\n  width:15px;\r\n  border-left:1px solid rgba(0,0,0,.4);\r\n  -webkit-border-radius:0 7px 7px 0;\r\n  -moz-border-radius:0 7px 7px 0;\r\n  border-radius:0 7px 7px 0;\r\n  -webkit-box-shadow:inset 1px 0 0 rgba(255,255,255,.1),-1px 0 0 rgba(255,255,255,.1);\r\n  -moz-box-shadow:inset 1px 0 0 rgba(255,255,255,.1),-1px 0 0 rgba(255,255,255,.1);\r\n  box-shadow:inset 1px 0 0 rgba(255,255,255,.1),-1px 0 0 rgba(255,255,255,.1);\r\n}\r\n\r\n.glossy-selectbox input:hover + label {\r\n  color:white;\r\n}\r\n\r\n.glossy-selectbox input:hover + label:after {\r\n  background-color:rgba(255,255,255,.04);\r\n}\r\n\r\n.glossy-selectbox ul {\r\n  margin:0 0;\r\n  padding:0 0;\r\n  position:absolute;\r\n  top:100%;\r\n  left:14px;\r\n  right:14px;\r\n  background-color:#222;\r\n  border:1px solid #111;\r\n  -webkit-box-shadow:0 1px 2px rgba(0,0,0,.4),0 5px 7px -2px rgba(0,0,0,.4);\r\n  -moz-box-shadow:0 1px 2px rgba(0,0,0,.4),0 5px 7px -2px rgba(0,0,0,.4);\r\n  box-shadow:0 1px 2px rgba(0,0,0,.4),0 5px 7px -2px rgba(0,0,0,.4);\r\n  visibility:hidden;\r\n  opacity:0;\r\n  z-index:99;\r\n}\r\n\r\n.glossy-selectbox li {\r\n  margin:0 0;\r\n  padding:0 0;\r\n  list-style:none;\r\n  float:left;\r\n  width:50%;\r\n  display:inline;\r\n}\r\n\r\n.glossy-selectbox a {\r\n  display:block;\r\n  position:relative;\r\n  color:#999;\r\n  text-decoration:none;\r\n  text-shadow:0 0 2px black;\r\n  line-height:30px;\r\n  border-top:1px solid #111;\r\n  border-right:1px solid #111;\r\n  padding:0 15px 0 32px;\r\n  -webkit-box-shadow:inset 0 0 0 1px #333;\r\n  -moz-box-shadow:inset 0 0 0 1px #333;\r\n  box-shadow:inset 0 0 0 1px #333;\r\n  /* Opera note: An unstable box shadow will appear if you don''t define the border radius less than 1 pixel. Weird! */\r\n  -webkit-border-radius:1px;\r\n  -moz-border-radius:1px;\r\n  border-radius:1px;\r\n}\r\n\r\n.glossy-selectbox a:nth-child(even) {\r\n  border-right-width:0;\r\n}\r\n\r\n.glossy-selectbox a:before {\r\n  content:"";\r\n  display:block;\r\n  width:16px;\r\n  height:16px;\r\n  position:absolute;\r\n  top:7px;\r\n  left:7px;\r\n  background-color:transparent;\r\n  background-repeat:no-repeat;\r\n  background-position:50% 0;\r\n}\r\n\r\n.glossy-selectbox a:hover:before {\r\n  background-position:50% 100%;\r\n}\r\n\r\n.glossy-selectbox .social-rss:before      {background-image:url(''img/social_rss.png'');}\r\n.glossy-selectbox .social-facebook:before {background-image:url(''img/social_facebook.png'');}\r\n.glossy-selectbox .social-twitter:before  {background-image:url(''img/social_twitter.png'');}\r\n.glossy-selectbox .social-google:before   {background-image:url(''img/social_google.png'');}\r\n\r\n.glossy-selectbox a:hover {\r\n  background-color:rgba(0,0,0,.2);\r\n  color:#ccc;\r\n}\r\n\r\n/* On click, then... */\r\n.glossy-selectbox input:checked + label {\r\n  color:rgba(255,255,255,.4);\r\n  -webkit-transition-duration:.4s;\r\n  -moz-transition-duration:.4s;\r\n  -ms-transition-duration:.4s;\r\n  -o-transition-duration:.4s;\r\n  transition-duration:.4s;\r\n}\r\n\r\n.glossy-selectbox input:checked + label:before {\r\n  content:attr(data-focus);\r\n}\r\n\r\n.glossy-selectbox input:checked + label:after {\r\n  background-color:rgba(0,0,0,.2);\r\n  -webkit-box-shadow:inset 1px 0 1px rgba(0,0,0,.4),-1px 0 0 rgba(255,255,255,.1);\r\n  -moz-box-shadow:inset 1px 0 1px rgba(0,0,0,.4),-1px 0 0 rgba(255,255,255,.1);\r\n  box-shadow:inset 1px 0 1px rgba(0,0,0,.4),-1px 0 0 rgba(255,255,255,.1);\r\n}\r\n\r\n.glossy-selectbox input:checked ~ ul {\r\n  visibility:visible;\r\n  opacity:1;\r\n}', ''),
(8, 1, 'CCTV', '<canvas id="canvas"></canvas>\r\n<div class="info">move your mouse<div>\r\n  \r\n<!--\r\n\r\nCookies? Oldschool!! \r\nI use CCTV cams to track my users! \r\nWatch your step, they are everywhere.\r\n\r\nThanks to codepen.io/AndyTheGiant for the smooth movement of the camera\r\n\r\n-->  \r\n  \r\n  ', '*{\r\n  margin: 0;\r\n	padding: 0\r\n}\r\nbody{\r\n  background: #fafafa;\r\n  font-family:Courier;\r\n}\r\ncanvas {\r\n	display: block;\r\n	position: relative;\r\n	left: 0;\r\n	top: 25px;\r\n}\r\n.info{\r\n  font-size:50px;\r\n  text-align:center;\r\n  margin-top: 50px;\r\n  width:40%;\r\n  margin: 50px auto 0 auto;\r\n  transition: opacity 2s;\r\n}', 'window.requestAnimFrame = (function() {\r\n	return window.requestAnimationFrame || window.webkitRequestAnimationFrame || window.mozRequestAnimationFrame || window.oRequestAnimationFrame || window.msRequestAnimationFrame ||\r\n	function(callback) {\r\n		window.setTimeout(callback, 1000 / 60);\r\n	};\r\n})();\r\n\r\nvar BigBrother = ( function() {\r\n  \r\n  "use strict";\r\n\r\n	var CCTVCam = {\r\n    viewPoint : {},\r\n		angle : 0,\r\n    newAngle : 0,\r\n		translation : {},\r\n		maxAngle : 0,\r\n		minAngle : 0,\r\n\r\n		init : function() {\r\n			this.viewPoint = {\r\n				x : 0,\r\n				y : 0\r\n			};\r\n			this.angle = 0;\r\n      this.newAngle = 0;\r\n			this.translation = {\r\n				x : 62.5,\r\n				y : 92\r\n			};\r\n			this.maxAngle = 2.85;\r\n			this.minAngle = -0.55;\r\n		},\r\n\r\n		draw : function() {\r\n      // smooth movement of the cam. Thanks Andy!\r\n        this.angle +=(this.newAngle-this.angle)/15;\r\n			ctx.beginPath()\r\n			// draw horizontal stab\r\n			ctx.rect(0, 70, 10, 45);\r\n			this.roundRect(9, 83.75, 10, 17.5, 2);\r\n			ctx.rect(18.5, 90, 37, 5);\r\n			// draw joint\r\n			ctx.arc(this.translation.x, this.translation.y, 7.5, 0, 2 * Math.PI);\r\n			ctx.save();\r\n			// translate to center of the joint\r\n			ctx.translate(this.translation.x, this.translation.y);\r\n			ctx.rotate(this.angle);\r\n			// draw vertical stab\r\n			ctx.rect(-2.5, -32.5, 5, 32.5);\r\n			this.roundRect(-8.5, -37.5, 17.5, 6, 2);\r\n			// draw cam\r\n			ctx.rect(-32, -64, 80, 27.5);\r\n			ctx.rect(48, -60, 5, 15);\r\n			ctx.rect(52, -57.25, 4, 10);\r\n			// restore initial state of canvas\r\n			ctx.restore();\r\n			ctx.fillStyle = ''#333'';\r\n			ctx.fill();\r\n		},\r\n		update : function(_angle) {\r\n      if (_angle < this.minAngle) {\r\n			  _angle = this.minAngle;\r\n			}\r\n			if (_angle > this.maxAngle) {\r\n				_angle = this.maxAngle;\r\n			}\r\n        this.newAngle = _angle;\r\n        // calculate absolute pos of the cam\r\n				this.viewPoint.x = this.translation.x - (50 * Math.cos(this.angle + (90 * Math.PI / 180)));\r\n				this.viewPoint.y = (this.translation.y + 25) - (50 * Math.sin(this.angle + (90 * Math.PI / 180)));\r\n\r\n		},\r\n		roundRect : function(x, y, width, height, radius) {\r\n			var r = x + width;\r\n			var b = y + height;\r\n			ctx.moveTo(x + radius, y);\r\n			ctx.lineTo(r - radius, y);\r\n			ctx.quadraticCurveTo(r, y, r, y + radius);\r\n			ctx.lineTo(r, y + height - radius);\r\n			ctx.quadraticCurveTo(r, b, r - radius, b);\r\n			ctx.lineTo(x + radius, b);\r\n			ctx.quadraticCurveTo(x, b, x, b - radius);\r\n			ctx.lineTo(x, y + radius);\r\n			ctx.quadraticCurveTo(x, y, x + radius, y);\r\n		}\r\n	}\r\n\r\n	var canvas;\r\n	var ctx;\r\n	var WIDTH;\r\n	var HEIGHT;\r\n	var mouse;\r\n  var isStart;\r\n\r\n	var init = function() {\r\n		canvas = document.getElementById(''canvas'');\r\n		ctx = canvas.getContext(''2d'');\r\n		canvas.width = WIDTH = 300;\r\n		canvas.height = HEIGHT = 300;\r\n		mouse = {\r\n			x : window.innerWidth / 2,\r\n			y : window.innerHeight / 2\r\n		};\r\n    isStart = true;\r\n		bindEventHandlers();\r\n		CCTVCam.init();\r\n		draw();\r\n	}\r\n		\r\n  var bindEventHandlers = function() {\r\n	  document.onmousemove = function(e) {\r\n		  mouse.x = e.pageX;\r\n			mouse.y = e.pageY;\r\n      if(isStart){\r\n        // fade out the info message\r\n        var infoElm = document.querySelector(''.info'');\r\n        infoElm.style.opacity = 0;\r\n      }\r\n      isStart = false;\r\n		};\r\n	}\r\n	\r\n  var update = function() {\r\n	  var deltaX = mouse.x - CCTVCam.translation.x;\r\n		var deltaY = mouse.y - (CCTVCam.translation.y + 25);\r\n		// distance between mouse and joint of the cam\r\n		var mag = Math.sqrt(deltaX * deltaX + deltaY * deltaY);\r\n\r\n		// only update cam if the mouse is further away as 50\r\n		if (mag > 50 && mouse.x > 20) {\r\n      var camPoint = CCTVCam.viewPoint;\r\n			deltaX = mouse.x - camPoint.x;\r\n			deltaY = mouse.y - camPoint.y;\r\n			// calculate angle between cam and mouse\r\n			var angleRad = Math.atan2(deltaY, deltaX);\r\n			CCTVCam.update(angleRad);\r\n		}\r\n	}\r\n		\r\n  var draw = function() {\r\n	  ctx.clearRect(0, 0, WIDTH, HEIGHT);\r\n		update();\r\n		CCTVCam.draw();\r\n		requestAnimFrame(draw);\r\n	}\r\n\r\n	return {\r\n	  init : init\r\n	}\r\n}());\r\n\r\nBigBrother.init(); ');
