rite"),\r\n	player, platformCount = 10,\r\n	position = 0,\r\n	gravity = 0.2,\r\n	animloop,\r\n	flag = 0,\r\n	menuloop, broken = 0,\r\n	dir, score = 0, firstRun = true;\r\n\r\n//Base object\r\nvar Base = function() {\r\n	this.height = 5;\r\n	this.width = width;\r\n\r\n	//Sprite clipping\r\n	this.cx = 0;\r\n	this.cy = 614;\r\n	this.cwidth = 100;\r\n	this.cheight = 5;\r\n\r\n	this.moved = 0;\r\n\r\n	this.x = 0;\r\n	this.y = height - this.height;\r\n\r\n	this.draw = function() {\r\n		try {\r\n			ctx.drawImage(image, this.cx, this.cy, this.cwidth, this.cheight, this.x, this.y, this.width, this.height);\r\n		} catch (e) {}\r\n	};\r\n};\r\n\r\nvar base = new Base();\r\n\r\n//Player object\r\nvar Player = function() {\r\n	this.vy = 11;\r\n	this.vx = 0;\r\n\r\n	this.isMovingLeft = false;\r\n	this.isMovingRight = false;\r\n	this.isDead = false;\r\n\r\n	this.width = 55;\r\n	this.height = 40;\r\n\r\n	//Sprite clipping\r\n	this.cx = 0;\r\n	this.cy = 0;\r\n	this.cwidth = 110;\r\n	this.cheight = 80;\r\n\r\n	this.dir = "left";\r\n\r\n	this.x = width / 2 - this.width / 2;\r\n	this.y = height;\r\n\r\n	//Function to draw it\r\n	this.draw = function() {\r\n		try {\r\n			if (this.dir == "right") this.cy = 121;\r\n			else if (this.dir == "left") this.cy = 201;\r\n			else if (this.dir == "right_land") this.cy = 289;\r\n			else if (this.dir == "left_land") this.cy = 371;\r\n\r\n			ctx.drawImage(image, this.cx, this.cy, this.cwidth, this.cheight, this.x, this.y, this.width, this.height);\r\n		} catch (e) {}\r\n	};\r\n\r\n	this.jump = function() {\r\n		this.vy = -8;\r\n	};\r\n\r\n	this.jumpHigh = function() {\r\n		this.vy = -16;\r\n	};\r\n\r\n};\r\n\r\nplayer = new Player();\r\n\r\n//Platform class\r\n\r\nfunction Platform() {\r\n	this.width = 70;\r\n	this.height = 17;\r\n\r\n	this.x = Math.random() * (width - this.width);\r\n	this.y = position;\r\n\r\n	position += (height / platformCount);\r\n\r\n	this.flag = 0;\r\n	this.state = 0;\r\n\r\n	//Sprite clipping\r\n	this.cx = 0;\r\n	this.cy = 0;